package operator;

public class UnaryOperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
			int x=10;
			System.out.println(++x); // evaluates x=x+1 and print updated value of x
			x=10;
			System.out.println(x++);// print x and evaluates x=x+1
			x=10;
			System.out.println(x--);// print x and evaluated x=x-1
			x=10;
			System.out.println(--x); // evaluated x=x-1 and prints the updated x value
			

	}

}
